  <Container $isPanelOpen={isPanelOpen}>
    <H2>All Gyms</H2>
    <GymList>
      {gyms.map((gym) => (
        <Gym key={gym.id} gym={gym} pickGym={pickGym}/>
      ))}
    </GymList>

  </Container>
)
      }
