import styled from 'styled-components'
import { Pill } from '../../styles'


export const SearchContainer = styled`
  border: 2px solid #000; 
  height: 20px; 
  width: 420px;
  background: fffff0;
  padding: 8px; 
  display: flex;
  align-items: center;
  justify-content: center;
  overflow: hidden;
`

export const Input = styled.input`
  font-family: 'Work Sans', sans-serif;
  font-weight: 700;
  font-size: 18px;
  flex-grow: 1;
  background: inherit;
  border: none;
  padding: 6px;

`
